const initialValue = 10;
const res = [1,2,3,4,5].reduce(function(accumulator, currentValue, currentIndex, array) {
  console.log(accumulator, currentValue, currentIndex, array);
  return accumulator + currentValue;
}, initialValue);
console.log(res);

/* 結果 */
